import{_ as i,a as t,H as r,S as o,D as n,o as s}from"./chunks/framework.DX1C7ho2.js";const f=JSON.parse('{"title":"Monthly Updates [August]","description":"September 2023 updates.","frontmatter":{"title":"Monthly Updates [August]","description":"September 2023 updates.","date":"2023-08-01T00:00:00.000Z","next":false,"aside":"right","prev":false,"footer":true},"headers":[],"relativePath":"posts/aug-2023.md","filePath":"posts/aug-2023.md"}'),d={name:"posts/aug-2023.md"};function l(p,e,h,u,m,c){const a=n("Post");return s(),t("div",null,[r(a,{authors:"nbats"}),e[0]||(e[0]=o('<div class="info custom-block"><p class="custom-block-title">INFO</p><p>These update threads only contains major updates. If you&#39;re interested in seeing all minor changes you can follow our <a href="https://github.com/Lost Pages/Lost Pagesedit/commits/main" target="_blank" rel="noreferrer">Commits Page</a> on GitHub or <a href="https://redd.it/17f8msf" target="_blank" rel="noreferrer">Updates Channel</a> in Discord.</p></div><h3 id="wiki-updates" tabindex="-1">Wiki Updates <a class="header-anchor" href="#wiki-updates" aria-label="Permalink to &quot;Wiki Updates&quot;">​</a></h3><ul><li><p>Moved Lost Pages.ml to <strong><a href="/">Lost Pages.net</a></strong>, and updated its UI to more closely match <a href="/">Lost Pages.pages.dev</a>.</p></li><li><p>Separated Android Tools into subcategories: <a href="/android-iosguide/#android-device">Device</a>, <a href="/android-iosguide/#android-utilities">Utilities</a>, <a href="/android-iosguide/#android-internet">Internet</a>, <a href="/android-iosguide/#android-camera">Camera</a> and <a href="/android-iosguide/#customization">Customization</a>.</p></li><li><p>Added a <a href="/edupiracyguide/#engineering">Engineering</a> section to Educational.</p></li><li><p>Renamed Multi Hosts to <a href="/videopiracyguide/#multi-server">Multi Server</a>, Dedicated Hosts to <a href="/videopiracyguide/#single-server">Single Server</a>, and bumped Multi above Single as they&#39;re generally better.</p></li><li><p>Moved all the APK sections out of storage, and back into the main <a href="/android-iosguide/#android-apks">Android section</a>.</p></li><li><p>Combined all <a href="/non-english/#indian-languages">Indian Language sites</a> into one section in Non-Eng, as most host content for more than one language.</p></li></ul><hr><h3 id="stars-added-⭐" tabindex="-1">Stars Added ⭐ <a class="header-anchor" href="#stars-added-⭐" aria-label="Permalink to &quot;Stars Added ⭐&quot;">​</a></h3><ul><li><p>Starred <a href="/gamingpiracyguide/#download-games">Ova Games</a> in Game Download. Big library, fast hosts.</p></li><li><p>Starred <a href="/videopiracyguide/#multi-server">SmashyStream</a> in Streaming Sites. Nice UI, fast 1080p.</p></li><li><p>Starred <a href="/storage/#game-libraries--launcher">BlackPearlOrigin</a> in Game Launchers. New game launcher and downloader, made by our friends.</p></li><li><p>Starred Androeed and Modyolo in <a href="/android-iosguide/#modded-apks">Modded APKs</a>.</p></li><li><p>Starred <a href="/gamingpiracyguide/#emulation--roms">Skraper</a> in Emulation. Automate ROM Covers / Metadata.</p></li><li><p>Starred <a href="/gamingpiracyguide/#rom-sites">ROMhacking</a> in ROM Sites. One of the oldest sites for fan translations.</p></li><li><p>Starred <a href="/videopiracyguide/#live-tv--sports">FreeSports</a> in Live Sports. A live sports site that isn&#39;t flooded with ads like most are.</p></li><li><p>Starred <a href="/videopiracyguide/#sports-streaming">SoccerCatch</a> in Sports Streaming. Full Match replays.</p></li><li><p>Starred <a href="/storage/#vfx-sites">DownloadPirate</a> in VFX Sites. Popular site for video effects and software.</p></li><li><p>Starred <a href="/storage/#windows-10-debloater">SophiApp</a> in debloaters. Gives good explanations for its tweaks making it safer for new users.</p></li><li><p>Starred <a href="https://ash.fail/blog/20230625-actually-good-distro-recomendations-for-beginners.html" target="_blank" rel="noreferrer">Actually Good Distros</a> in Linux. Beginner distro guides.</p></li></ul><hr><h3 id="things-removed" tabindex="-1">Things Removed <a class="header-anchor" href="#things-removed" aria-label="Permalink to &quot;Things Removed&quot;">​</a></h3><ul><li><p>Removed Bromite as they&#39;ve <a href="https://i.imgur.com/uM3ryHR.png" target="_blank" rel="noreferrer">stopped updates</a>, and replaced it with <a href="https://github.com/uazo/cromite" target="_blank" rel="noreferrer">Cromite</a> which will continue to be updated by one of their devs.</p></li><li><p>Removed AI Text Detectors as they&#39;re known for <a href="https://arstechnica.com/information-technology/2023/07/openai-discontinues-its-ai-writing-detector-due-to-low-rate-of-accuracy/" target="_blank" rel="noreferrer">being inaccurate</a>.</p></li><li><p>Unstarred SoftArchive in Software as people have become annoyed with their slow hosts.</p></li><li><p>Unstarred LookMovie in Streaming as people don&#39;t like the fact 1080p is behind premium.</p></li><li><p>Unstarred QuickBoost in Tweaking as the app doesn&#39;t give details as to what each tweak actually does.</p></li><li><p>Unstarred Sockshare in Streaming as their original domain died, and it doesn&#39;t have the best UI anyways.</p></li><li><p>Removed AnimeKaizoku from Anime Streaming as they&#39;ve shutdown.</p></li><li><p>Removed DeepFloyd from Image Generators as its been paused for over a month.</p></li><li><p>Removed Appnee from Software Sites. The MAS team found that they host many <a href="https://i.imgur.com/KmDKUw4.png" target="_blank" rel="noreferrer">unsafe activators</a>. We don&#39;t think Appnee is purposefully malicious, they just need to be more careful and clean things up before we can add them again.</p></li></ul>',9))])}const v=i(d,[["render",l]]);export{f as __pageData,v as default};
